package com.sugar.grapecollege.common.pay.model;import android.text.TextUtils;import java.util.Map;/** * @CreateBy qsmaxmin * @Date 2017/5/5 13:22 * @Description 支付结果model解析 * <p> * { * "memo" : "xxxxx", * "result" : "{ * "alipay_trade_app_pay_response":{ * "code":"10000", * "msg":"Success", * "app_id":"2014072300007148", * "out_trade_no":"081622560194853", * "trade_no":"2016081621001004400236957647", * "total_amount":"0.01", * "seller_id":"2088702849871851", * "charset":"utf-8", * "timestamp":"2016-10-11 17:43:36" * }, * "sign":"NGfStJf3i3ooWBuCDIQSumOpaGBcQz+aoAqyGh3W6EqA/gmyPYwLJ2REFijY9XPTApI9YglZyMw+ZMhd3kb0mh4RAXMrb6mekX4Zu8Nf6geOwIa9kLOnw0IMCjxi4abDIfXhxrXyj********", * "sign_type":"RSA2" * }", * "resultStatus" : "9000" * } */public class AliPayResult {    /**     * resultStatus:     * 9000	订单支付成功     * 8000	正在处理中，支付结果未知（有可能已经支付成功），请查询商户订单列表中订单的支付状态     * 4000	订单支付失败     * 5000	重复请求     * 6001	用户中途取消     * 6002	网络连接出错     * 6004	支付结果未知（有可能已经支付成功），请查询商户订单列表中订单的支付状态     * 其它	其它支付错误     */    private String resultStatus;    private String result;    private String memo;    public AliPayResult(Map<String, String> rawResult) {        if (rawResult == null) {            return;        }        for (String key : rawResult.keySet()) {            if (TextUtils.equals(key, "resultStatus")) {                resultStatus = rawResult.get(key);            } else if (TextUtils.equals(key, "result")) {                result = rawResult.get(key);            } else if (TextUtils.equals(key, "memo")) {                memo = rawResult.get(key);            }        }    }    @Override public String toString() {        return "resultStatus={" + resultStatus + "};memo={" + memo + "};result={" + result + "}";    }    /**     * @return the resultStatus     */    public String getResultStatus() {        return resultStatus;    }    /**     * @return the memo     */    public String getMemo() {        return memo;    }    /**     * @return the result     */    public String getResult() {        return result;    }}